async function loadSessionHistory(sessionId: string, beadId: string) {
  // Read from ~/.bp6/sessions/<bead-id>/<session-id>-*.jsonl
  const logDir = `${HOME}/.bp6/sessions/${beadId}`;
  const logFiles = await readDir(logDir);
  const sessionLogs = logFiles.filter(f => f.startsWith(sessionId));

  // Parse JSONL and reconstruct conversation
  const messages = [];
  for (const line of sessionLogs) {
    const entry = JSON.parse(line);
    if (entry.event_type === 'message' || entry.event_type === 'chunk') {
      messages.push(entry);
    }
  }
  return messages;
}
