## Handler Implementations
```typescript
const handleSessionSwitch = async (sessionId: string) => {
  if (sessionId === activeSessionId) return;

  try {
    setIsLoading(true);
    await switchActiveSession(sessionId);

    // Load conversation history from JSONL
    const history = await loadSessionHistory(sessionId, beadId);
    setMessages(history);
    setSessionId(sessionId);

    setDebugLogs(prev => [...prev, `[System] Switched to session ${sessionId}`]);
  } catch (error) {
    console.error('Failed to switch session:', error);
    setDebugLogs(prev => [...prev, `[Error] Switch failed: ${error}`]);
  } finally {
    setIsLoading(false);
  }
};

const handleSessionTerminate = async (sessionId: string) => {
  // Show confirmation dialog
  const confirmed = window.confirm(
    `Terminate session ${sessionId}? This will stop the agent and close the session.`
  );
  if (!confirmed) return;

  try {
    await terminateSession(sessionId);

    // If we terminated the active session, switch to another or clear
    if (sessionId === activeSessionId) {
      const sessions = await listActiveSessions();
      if (sessions.length > 0) {
        await handleSessionSwitch(sessions[0].session_id);
      } else {
        setMessages([]);
        setSessionId(null);
      }
    }

    setDebugLogs(prev => [...prev, `[System] Terminated session ${sessionId}`]);
  } catch (error) {
    console.error('Failed to terminate session:', error);
    setDebugLogs(prev => [...prev, `[Error] Termination failed: ${error}`]);
  }
};
```

