// In window.rs
pub async fn on_window_close(
    app: AppHandle,
    window_label: String,
    registry: State<WindowRegistry>,
) {
    // Find session_id for this window
    let session_id = registry.get_session_by_window(&window_label);

    // Unregister window (but don't terminate session)
    registry.unregister_window(&session_id);

    // Emit event so UI can update
    app.emit_all("session-window-closed", WindowClosedPayload {
        session_id: session_id.clone(),
        window_label,
    }).unwrap();
}
