                        if now.duration_since(*last) >= Duration::from_millis(250) {
                            *last = now;
                            let _ = handle.emit("beads-updated", ());
                        }
                    }
                }
            }
        },
        Err(e) => eprintln!("Beads file watch error: {:?}", e),
    }
}, Config::default()).unwrap();
